Create table Products (
    id serial Primary key,
    type, text,
    subtype text,
    image varchar(10000),
    image2 varchar(10000),
    description text,
    details text,
    name varchar(200),
    price decimal
)

create table users (
    id serial primary key,
    user_name varchar(180),
    email varchar(180),
    picture text,
    auth_id text
);

Create table Cart(
    id serial Primary key,
    product_id integer,
    user_id integer,
    image text,
    name text, 
    price integer

)










